import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Card, CardMedia, CardContent, Typography } from "@material-ui/core";
import { useSelector } from "react-redux";
const useStyles = makeStyles({
  media: {
    width: 300,
  },
});
const PersonCard = () => {
  const classes = useStyles();
  const { details } = useSelector((state) => state.taskReducer);

  const { id, email, first_name, last_name, avatar } = details;
  return (
    <>
      <Card sx={{ maxWidth: 345 }}>
        <h4>
          {first_name} {last_name}
        </h4>
        <CardMedia
          className={classes.media}
          component="img"
          height="240"
          alt="0"
          image={avatar}
        />
        <CardContent>
          <Typography variant="h5">ID : {id}</Typography>
          <Typography variant="h6">First Name : {first_name}</Typography>
          <Typography variant="h6">Last Name : {last_name}</Typography>
          <Typography variant="h6">Email : {email}</Typography>
        </CardContent>
      </Card>
    </>
  );
};

export default PersonCard;
