import React, { useEffect, useState } from "react";
import axios from "axios";
import { CircularProgress } from "@material-ui/core";
import { setApiData, findById } from "../src/app/actions/Actions";
import { useSelector, useDispatch } from "react-redux";
import { Button } from "@material-ui/core";
import "./App.css";
import PersonCard from "./component/PersonCard";

const App = () => {
  console.log("App rendere");
  const { apidata } = useSelector((state) => state.taskReducer);
  const dispatch = useDispatch();

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(null);

  const fetchData = async () => {
    setLoading(true);
    const infodata = await axios.get("https://reqres.in/api/users");
    setTimeout(() => {
      setLoading(false);
    }, 500);
    console.log(loading);
    setData(infodata.data.data);
    dispatch(setApiData(infodata.data.data));
  };
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      {loading ? (
        <CircularProgress color="secondary" size={104} />
      ) : (
        <h1>Please Click </h1>
      )}
      <PersonCard  data={data} />
      <div className="page-div">
        {data?.map((place, i) => (
          <div className="page-idx" key={i}>
            <Button
              variant="contained"
              key={i}
              onClick={() => dispatch(findById(i))}
            >
              {i + 1}
            </Button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
