const InitialState = {
  apidata: [],
};
const TaskReducer = (state=InitialState, action) => {
  switch (action.type) {
    case "FETCH_DATA":
     return {

       apidata: action.payload,
     };
      
    case "LOAD_PERSON_DATA":
      return {
        state,
      };
    default:
    return state;
    
  }
};
export default TaskReducer;
