const InitialState = {
  apidata: [],
};
const TaskReducer = (state = InitialState, action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return {
        ...state,
        apidata: action.payload,
      };

    case "FIND_BY_ID":
    
      const data = state.apidata.filter((ele, id) => ele.id === 2);
      console.log(action.id);
      return {
      ...state
      };

    default:
      return state;
  }
};
export default TaskReducer;
