const InitialState = {
  apidata: [],
};
const TaskReducer = (state=InitialState, action) => {
  switch (action.type) {
    case "FETCH_DATA":
     return {
       apidata: action.payload,
     };
      
    case "FIND_BY_ID":
      const delet = state.apidata.filter((ele) => ele.id !== action.id);
      console.log(action.id);
      return {
        ...state,

      };
    default:
    return state;
    
  }
};
export default TaskReducer;
