import React, { useEffect, useState } from "react";
import axios from "axios";
import {setApiData} from "../src/app/actions/Actions"
import{useSelector,useDispatch}from "react-redux"
import { Button } from "@material-ui/core";
import "./App.css";
import PersonCard from "./component/PersonCard";

const App=()=> {
 const data=useSelector((state)=>state);
 const dispatch=useDispatch();
  const [info, Setinfo] = useState([]);
  const [index, setIndex] = useState(null);
  const fetchData = async () => {
    const infodata = await axios.get("https://reqres.in/api/users");
    Setinfo(infodata.data.data);
    dispatch(setApiData(infodata.data.data));
  };
  console.log(data.apid);
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      <PersonCard  id={index}/>
      <div className="page-div">
        {info?.map((place, i) => (
          <div className="page-idx"  key={i}>
            <Button variant="contained" key={i} onClick={() => setIndex(i)}>
              {i + 1}
            </Button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
