import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Card, CardMedia, CardContent, Typography } from "@material-ui/core";
import { useSelector} from "react-redux";
const useStyles = makeStyles({
  media: {
    width: 300,
  },
});
const PersonCard = ({ id, firstName, lastName, email, imgUrl }) => {
  const classes = useStyles();
  const { apidata } = useSelector((state) => state.taskReducer);
  console.log(first)
  return (
    <>
      <Card sx={{ maxWidth: 345 }}>
        <h4>
          {firstName} {lastName}
        </h4>
        <CardMedia
          className={classes.media}
          component="img"
          height="240"
          alt="0"
          image={imgUrl}
        />
        <CardContent>
          <Typography variant="h5">ID : {id}</Typography>
          <Typography variant="h6">First Name : {firstName}</Typography>
          <Typography variant="h6">Last Name : {lastName}</Typography>
          <Typography variant="h6">Email : {email}</Typography>
        </CardContent>
      </Card>
    </>
  );
};

export default PersonCard;
