import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Card, CardMedia, CardContent, Typography } from "@material-ui/core";
const useStyles = makeStyles({
  media: {
   
    width:340,
  },
});
const PersonCard = ({ id }) => {
    const classes = useStyles();
  const firstName = "Akash";
  const lastName = "kumar";
  const email = "akmailfasdffsdfcom";
  
  return (
    <>
      <Card sx={{ maxWidth: 345 }}>
         <h4><span>   </span>{firstName}{lastName}</h4>
        <CardMedia
          className={classes.media}
          component="img"
          height="240"
          alt="green iguana"
          image="https://reqres.in/img/faces/7-image.jpg"
        />
        <CardContent>
          <Typography variant="h4">
            ID : {id} <span> gv </span>
          </Typography>
          <Typography variant="h6">First Name : {firstName}</Typography>
          <Typography variant="h6">Last Name : {lastName}</Typography>
          <Typography variant="h6">Email : {email}</Typography>
        </CardContent>
      </Card>
    </>
  );
};

export default PersonCard;
