const InitialState = {
  apidata: [],
  DetailbyId: {},
};
const TaskReducer = (state = InitialState, action) => {
  switch (action.type) {
    case "FETCH_DATA":
      return {
        ...state,
        apidata: action.payload,
      };

    case "FIND_BY_ID":
      co
      const data = state.apidata.filter((ele, idx) => idx === action.id);
      return {
        ...state,
        DetailbyId:data
      };

    default:
      return state;
  }
};
export default TaskReducer;
